import 'package:firebase_auth/firebase_auth.dart';import 'package:flutter/material.dart';class UserPage extends StatefulWidget {  final UserCredential credential;  const UserPage({super.key, required this.credential});  @override  State<UserPage> createState() => _UserPageState();}class _UserPageState extends State<UserPage> {  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: const Text('User Page'),      ),      body: Column(        mainAxisAlignment: MainAxisAlignment.center,        crossAxisAlignment: CrossAxisAlignment.center,        children: [          Text(            widget.credential.user!.email.toString(),          ),          SizedBox(height: 20,),          Text(            widget.credential.user!.displayName.toString(),          ),        ],      ),    );  }}